export default(state, action)=>{
    switch(action.type){
        case 'DELETE_TRANSACTION':
        return{
            ...state,
            transaction: state.transactions.filter(transaction=> transaction.id !== action.payload)
        }
        case 'ADD_TRANSACTION':
            return{}
        default:
            return state;
    }
}
